import React, { useState, useEffect } from 'react'

// import Products from './components/Products/Products';
// import Navbar from './components/Navbar/Navbar';

// the import on top is same as below which is more simple
//In order to import like below, need to export default and create index.js inside components file
//The curly brace means name

import { commerce } from './lib/commerce';
import { Products, Navbar} from './components';

const App = () => {
    const [products, setProducts] = useState([]);
    const [cart, setCart] = useState({})

    const fetchProducts = async () => {
        const { data } = await commerce.products.list();

        setProducts(data);
    }

    const fetchCart = async () => {
        setCart(await commerce.cart.retrieve())
    }

    const handleAddToCart = async (productId, quantity) => {
        const item = await commerce.cart.add(productId, quantity);

        setCart(item.cart);
    }

    useEffect (() => {
        fetchProducts();
        fetchCart();
    }, []);

    console.log(cart);

     //To render navbar, products, etc.
    return (
        <div>
            <Navbar totalItems={cart.total_items} />
            <Products products={products} onAddToCart={handleAddToCart} />
        </div>
    )
}

export default App
